import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props) {
  return (
    <button className="square" onClick={props.onClick}>
      {showMark(props.value)}
    </button>
  );
}

class Board extends React.Component {
  renderSquare(x,y) {
    return (
      <Square 
        value={this.props.squares[y][x]} 
         onClick={()=>{
           this.props.onClick(x,y);

         }}
        />
    );
  }

  render() {
    const tableSquare = ([...Array(8).keys()]).map(row => (
      <div className="board-row" key={row}>
        {[...Array(8).keys()].map(col => this.renderSquare(col, row))}
      </div>
    ));
    return (
      <div>
        {tableSquare}
      </div>
    );
  }
}

const TABLE_SIZE = 8;
const WHITE_MARK_ID = 1;
const BLACK_MARK_ID = 2;
const INIT_TABLE = 
[ 
  [null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null],
  [null, null, null,    1,    2, null, null, null],
  [null, null, null,    2,    1, null, null, null],
  [null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null],
  [null, null, null, null, null, null, null, null],
];

class Game extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      history: [{
        squares: INIT_TABLE
      }],
      stepNumber: 0,
      xIsNext: true
    };
  }

  getFlippableList(squares, x, y, player_id) {
    const REV = -1;
    const CURRENT = 0;
    const NEXT = 1;
    const DIRECTIONS = [REV, CURRENT, NEXT];
    let flippableList = [];

    DIRECTIONS.forEach((dy) => {
      DIRECTIONS.forEach((dx) => {

        if( dx === CURRENT && dy === CURRENT ) {
          return;
        }
        let tmp = [];
        let depth = 0;

        while( true ) { 
          depth += 1;

          const rx = x + dx*depth;
          const ry = y + dy*depth;
          if( rx < 0 || TABLE_SIZE <= rx || ry < 0 || TABLE_SIZE <= ry ) {
            break;
          }
          let value = squares[ry][rx];

          if( value === null ) {
            break;
          }

          if( value === player_id ) {
            if( tmp.length !== 0 ) {
              flippableList = flippableList.concat(tmp);
            }
            break;
          }
          else {
            tmp.push([rx, ry]);
          }
        }
      });
    });
    return flippableList;
  }

  handleClick(x, y) {
    const history = this.state.history.slice(0, this.state.stepNumber+1);
    const current = this.state.history[history.length-1];
    const squares = current.squares.slice();
    if( squares[y][x] ) {
      return;
    }

    const currentPlayer = this.state.xIsNext ? WHITE_MARK_ID : BLACK_MARK_ID;
    const flippableList = this.getFlippableList(squares, x, y, currentPlayer);

    if( flippableList.length === 0 ) {
      return;
    }

    squares[y][x] = currentPlayer;
    flippableList.forEach((position) => {
      squares[position[1]][position[0]] = currentPlayer;
    });

    this.setState({
      history: history.concat([{
        squares: squares
      }]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext
    });
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: (step % 2) === 0
    });
  }

  getPoint(squares, playerId) {
    let count = 0;
    for(let i=0; i<squares.length; i++) {        
      if( squares[i] === playerId ) {
        count += 1;
      }
    }
    return count;
  }

  showPlayerPoints(squares) {
    firstPlayer = this.getPoint(squares, BLACK_MARK_ID);
    secondPlayer = this.getPoint(squares, WHITE_MARK_ID);
  }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = false;

    const moves = history.map((step, move) => {
      const desc = move ?
      'go to move #' + move :
      'go to game start';
      return (
        <li key={move}>
          <button onClick={()=>this.jumpTo(move)} className="move-list">{desc}</button>
        </li>
      );
    });
    let status;
    if( winner ) {
      status = 'winner: ' + winner;
    }
    else {
      const nextplayer = (this.state.xIsNext ? WHITE_MARK_ID : BLACK_MARK_ID);
      console.log("ðŸš€ ~ file: index.js ~ line 172 ~ Game ~ render ~ nextplayer", nextplayer);
      status = 'next player: ' + showMark(nextplayer);
    }

    return (
      <div className="game">
        <div className="game-board">
          <Board 
            squares={current.squares}
            onClick={(x,y)=>{
              this.handleClick(x,y);
              }}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{moves}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

ReactDOM.render(
  <Game />,
  document.getElementById('root')
);


function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];

  for( let i=0; i<lines.length; i++) {
    const [a, b, c] = lines[i];
    if( squares[a] && squares[a] === squares[b] && squares[a] === squares[c] ) {
      return squares[a];
    }
  }
  return null;
}
function showMark( markId ) {
  if(markId === BLACK_MARK_ID)
    return 'â—‹';
  else if (markId === WHITE_MARK_ID)
    return 'â—';
  else 
    return null
}
